class Solution:
    def findTheInteger(self, nums1: list[int], nums2: list[int]) -> int:
        # If nums2 is generated by adding an integer x to each element of nums1
        # and then randomly shuffling the resulting array,
        # it means that the set of elements in nums2 is the same as
        # the set of elements in nums1 after adding x to each.
        #
        # This implies that the minimum element of nums1 plus x must be
        # the minimum element of nums2.
        #
        # So, min(nums1) + x = min(nums2)
        # Therefore, x = min(nums2) - min(nums1)

        min_nums1 = min(nums1)
        min_nums2 = min(nums2)

        x = min_nums2 - min_nums1

        return x